const port_node_red = configuration.ports.nodeRed;

var endpoint = "v1/json";

let element = msg.element;

/* Only try to connect to the local API when EcoTracker is connected to the cloud. 
   This is to ensure that the local IP address has been synchronized correctly. */
if (element.states.connected){
    msg.instance_active = true;
    const ip_address = element["ip-wifi"];
    let url = `http://${ip_address}/${endpoint}`;
    const url_simulation = `http://${ip_address}:${port_node_red}/endpoint/${endpoint}`;

    if (global.get("add_simu_devices")) {
        msg.url = url_simulation;
    }else{
        msg.url = url;
    }
    return msg;
}

function getValueFromConfig(valuesArray, defaultValue, deviceId) {
    for (let i = 0; i < valuesArray.length; i++) {
        if (valuesArray[i].id === deviceId) {
            return valuesArray[i].value;
        }
    }
    return defaultValue;
}

const FACTOR = 1000;
const configuration = global.get('configuration');

let valuesArray = configuration.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id;
let defaultValue = configuration.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.default_value;
let deviceId = msg.element.id;

const scan_interval = getValueFromConfig(valuesArray, defaultValue, deviceId);

if (msg.statusCode !== 200) {
    msg.delay = scan_interval * FACTOR * msg.counter;
    msg.counter ++;
    node.log(`Server Error: ${msg.url} - Status: ${msg.statusCode}. Next try in ${msg.delay / 1000} seconds`);
}else{
    msg.counter = 1;
    msg.delay = scan_interval * FACTOR;
}

return msg;

let states = {};

if (msg.statusCode === 200){
    var element_data = JSON.parse(msg.payload);
    states["energyCounterIn"] = parseFloat(element_data.energyCounterIn).toFixed(2); 
    states["energyCounterOut"] = parseFloat(element_data.energyCounterOut).toFixed(2); 
    states["power"] = element_data.power;
    states["local_api_connected"] = true;
}else{
    states["local_api_connected"] = false;
}

msg.element.local_api_connected = states["local_api_connected"];
let obj = {
    "states": states,
    "id": msg.element.id,
    "type": msg.element.type
};

msg.data = obj;
return msg;

