const groups = msg.groups;

function findGroupByDeviceID(groupList, deviceID) {
    return groupList.find(group => group.id === deviceID) || null;
}

function extractEntities(groupList, groupID, result = []) {
    const group = findGroupByDeviceID(groupList, groupID);
    if (group) {
        group.entities.forEach(entity => {
            if (entity.groupid) {
                extractEntities(groupList, entity.groupid, result);
            } else {
                result.push({ deviceid: entity.deviceid });
            }
        });
    }
    return result;
}

const updatedEntities = [];
groups.forEach(function (element) {
    if (element.entities) {
        const newEntities = [];
        element.entities.forEach(function (entity) {
            if (entity.groupid) {
                const extractedEntities = extractEntities(groups, entity.groupid);
                newEntities.push(...extractedEntities);
            } else {
                newEntities.push(entity);
            }
        });
        element.entities = newEntities;
        updatedEntities.push(...newEntities.filter(entity => !entity.groupid));
    }
});

msg.payload = groups;

return msg;

function groupBySubtype(groups) {
    const groupsBySubtype = {};

    groups.forEach(group => {
        const subtype = group.subtype;

        if (groupsBySubtype[subtype]) {
            groupsBySubtype[subtype].push(group);
        } else {
            groupsBySubtype[subtype] = [group];
        }
    });

    const result = [];
    for (const subtype in groupsBySubtype) {
        result.push({
            subtype: subtype,
            group_elements: groupsBySubtype[subtype]
        });
    }

    return result;
}

const groups = msg.payload;

const configuration = global.get('configuration');
// remove excluded groups
let groupsToRemove = [];
groups.forEach(function (element, index) {
    if (configuration.devices.exclude.some(excludeItem => excludeItem.subtype === element.subtype)) {
        groupsToRemove.push(index);
    }
});
groupsToRemove.reverse().forEach(function (index) {
    groups.splice(index, 1);
});

const groupsBySubtype = groupBySubtype(groups);

msg.payload = groupsBySubtype;

return msg;

const groups = msg.payload;
const language = msg.language;

// create availability list
//general
let avty_states_connected = [];
avty_states_connected.push(
    {
        "t": "everhome/central/state_connected/websocket",
        "val_tpl": `{{'online' if value_json.value == 'online' else 'offline'}}`
    }
)
avty_states_connected.push(
    {
        "t": "everhome/central/state_connected/mqtt",
        "val_tpl": `{{'online' if value_json.value == 'online' else 'offline'}}`
    }
)

groups.forEach(function (element, index) {
    element.type = "group";
    element.group_subtype = "maingroup";
    const main_name = ((text, targetLanguage) => global.get("translations")[text]?.[targetLanguage] || global.get("translations")[text]?.en || text)(element.type, global.get("language"));
    const sub_name = ((text, targetLanguage) => global.get("translations")[text]?.[targetLanguage] || global.get("translations")[text]?.en || text)(element.subtype, global.get("language"));
    element.device_name = `${sub_name} ${main_name}`;
    element.language = language;
    element.manufacturer = "everHome2MQTT";
    element.model = "Group";
    element.via_device = "everhome_central";
    element.main_topic = "everhome/group";
    element.config = {};
    element.uniq_id_main = `everhome_maingroup_${element.subtype}`;
    element.config.avty = avty_states_connected;
    element.states = {};
    element.ts = {};
    element.id = index + 1;

    element.device_id = `everhome_maingroup_${element.subtype}_${element.id}`;
});

msg.payload = groups;

return msg;

function findGatewayId(devices, deviceid) {
    for (let i = 0; i < devices.length; i++) {
        if (devices[i].id === deviceid) {
            return devices[i].gatewayid;
        }
    }
    return null;
}

const groups = msg.payload;
const devices = msg.devices;

// create availability list
//general
let avty_states_connected = [];
avty_states_connected.push(
    {
        "t": "everhome/central/state_connected/websocket",
        "val_tpl": `{{'online' if value_json.value == 'online' else 'offline'}}`
    }
)
avty_states_connected.push(
    {
        "t": "everhome/central/state_connected/mqtt",
        "val_tpl": `{{'online' if value_json.value == 'online' else 'offline'}}`
    }
)

// Search for the corresponding gateway of each device from the group
groups.forEach(function (element) {
    element.group_elements.forEach(function (element) {
        element.config = {};
        const avty = [...avty_states_connected];
        if (element.entities) {
            element.entities.forEach(function (entity) {
                const gatewayid = findGatewayId(devices, entity.deviceid);
                const newEntry = {
                    "t": `everhome/device/${gatewayid}/states/connected`,
                    "val_tpl": `{{'online' if value_json.value is defined else 'offline'}}`
                };
                if (!avty.some(item => item.t === newEntry.t && item.val_tpl === newEntry.val_tpl)) {
                    if (gatewayid !== undefined) {
                        avty.push(newEntry);
                    }
                }
            });
        }
        element.config.avty = avty;
    });

});

msg.payload = groups;

return msg;

function findeNameById(array, id) {
    for (let i = 0; i < array.length; i++) {
        if (array[i].id === id) {
            return array[i].name;
        }
    }
    return '';
}
function formatString(inputString, variables) {
    var regex = /<([^>]+)>/g;
    var result = inputString.replace(regex, function (match, placeholder) {
        return variables[placeholder] !== undefined ? variables[placeholder] : '';
    });
    return result;
}

const language = msg.language;
let rooms = msg.rooms;
const groups = msg.payload;
const configuration = msg.configuration;

const set_object_id = configuration.devices.set_object_id;

groups.forEach(function (element) {
    element.group_elements.forEach(function (group_element) {

        group_element.group_subtype = "groupelement";
        group_element.set_object_id = set_object_id;
        group_element.device_id = `everhome_maingroup_${group_element.subtype}_${element.id}`;
        group_element.uniq_id_main = `everhome_${group_element.type}_id_${group_element.id}`;
        group_element.room_name = findeNameById(rooms, group_element.roomid);

        var variables = {
            room_name: group_element.room_name,
            device_name: group_element.name
        };

        const inputString = configuration.devices?.naming ?? group_element.room_name + " - " + group_element.name;
        group_element.device_name = formatString(inputString, variables);

    });

});

msg.payload = groups;

return msg;

