function isInRange(num, min, max) {
    return num >= min && num <= max;
}
function convertToBoolean(input) {
    if (!isNaN(parseInt(input))) {
        return parseInt(input) === 1 ? true : (parseInt(input) === 0 ? false : null);
    }
    input = input.trim();
    if (input === "true") {
        return true;
    } else if (input === "false") {
        return false;
    } else {
        return null;
    }
}
function isEmptyObject(obj) {
    for (var key in obj) {
        if (obj.hasOwnProperty(key)) {
            return false;
        }
    }
    return true;
}

