        config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval = defaultConfig.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval;
        node.log("devices.subtype.electricitymeter.ecotracker.local_api.scan_interval is not an object, setting default configuration.");
    }

    // devices.subtype.electricitymeter.ecotracker.local_api.scan_interval
    if (isEmptyObject(config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval)) {
        config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval = defaultConfig.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval;
        node.log("devices.subtype.electricitymeter.ecotracker.local_api.scan_interval is empty, setting default configuration.");
    }

    // devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id
    if (!Array.isArray(config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id)) {
        config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id = defaultConfig.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id;
        node.log("devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id is not an array, setting default configuration.");
    }

    // devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id  - Array - id isInteger
    config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id.forEach(function (element, index) {
        if (!Number.isInteger(element.id)) {
            let value = parseInt(element.id);
            if (!isNaN(value)) {
                element.id = value;
            } else {
                config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id.splice(index, 1);
                node.log(`devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id[${index}].id ${element.id} is not an Integer(allowed example: 10 | "10" | "10abc"), Index has been removed from the configuration.`);
            }
        }
    });

    // devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id  - Array - value isInteger
    config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id.forEach(function (element, index) {
        if (!Number.isInteger(element.value)) {
            let value = parseInt(element.value);
            if (!isNaN(value)) {
                element.value = value;
            } else {
                node.log(`devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id[${index}].value ${element.value} of id ${element.id} is not Integer (allowed example: 10 | "10" | "10abc"), setting default configuration.`);
                element.value = defaultConfig.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.default_value;
            }
        }
    });

    // devices.subtype.motiondetector.reset_after.device_id  - Array - value out of range
    config.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id.forEach(function (element, index) {
        if (!isInRange(element.value, MIN_SCAN_INTERVAL, MAX_SCAN_INTERVAL)) {
            node.log(`devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.device_id[${index}].value ${element.value} of id ${element.id} is out of range. Min = ${MIN_SCAN_INTERVAL} | Max = ${MAX_SCAN_INTERVAL}. Value was set to default value.`);
            element.value = defaultConfig.devices.subtype.electricitymeter.ecotracker.local_api.scan_interval.default_value;
        }
    });

    // devices.exclude   
    if (!Array.isArray(config.devices.exclude)) {
        config.devices.exclude = [];
        node.log("devices.exclude is not an array, setting empty configuration.");
    }

    return config;
}

