function checkOnOffKeysExist(actions) {
    let onExists = false;
    let offExists = false;
    for (let i = 0; i < actions.length; i++) {
        const action = actions[i];
        if (action.key === 'on') {
            onExists = true;
        } else if (action.key === 'off') {
            offExists = true;
        }
        if (onExists && offExists) {
            break;
        }
    }
    return onExists && offExists;
}


